mapper.select:
  
		SELECT COUNT(*) FROM goods where 1=1
		
		AND typeNo=#{typeNo}
		
		
		AND goodsName LIKE CONCAT ("%",#{goodsName},"%")

SELECT g.goodsNo, g.goodsName, g.goodsNorm, g.goodsUnit, g.typeNo, t.typeName, s.supplierName, g.goodsInprise, g.goodsOutprise
FROM commodity_database.goods AS g INNER JOIN `type` AS t ON g.typeNo=t.typeNo INNER JOIN supplier AS s ON g.supplierNo=s.supplierNo WHERE 1=1
AND g.typeNo=#{typeNo}
AND g.goodsName LIKE CONCAT('%',#{goodsName},'%')
ORDER BY goodsNo ASC LIMIT #{pageNo},#{pageSize}

SELECT g.goodsNo, g.goodsName, g.goodsInprise, g.goodsOutprise, t.typeName, s.supplierName, g.goodsCount, g.goodsnorm FROM commodity_database.goods AS g INNER JOIN `type` AS t ON g.typeNo=t.typeNo INNER JOIN supplier AS s ON g.supplierNo=s.supplierNo WHERE 1=1
AND g.typeNo=#{typeNo}
AND g.goodsName LIKE CONCAT('%',#{goodsName},'%')
ORDER BY goodsNo ASC LIMIT #{pageNo},#{pageSize}
